mapper.select:
select count(*) from
users__
where id = #{id} and 


	pwd = #{pwd}


	name = #{name}

select idx, id, pwd, name, reg_date, end_date, hspt_name, hspt_subcode, hspt_subname, hspt_position, hspt_V, 
hspt_K, hspt_B, hspt_X, hspt_Z, hspt_S, hspt_T, user_code
from users__ where idx=#{idx}

select idx, id, pwd, name, reg_date, end_date, hspt_name, hspt_subcode, hspt_subname, hspt_position, hspt_V, 
hspt_K, hspt_B, hspt_X, hspt_Z, hspt_S, hspt_T, user_code
from users__ where id=#{id}

select idx, id, pwd, name, reg_date, hspt_name, hspt_subname, hspt_subcode, hspt_position, hspt_V, 
hspt_K, hspt_B, hspt_X, hspt_Z, user_code
from users__ where user_code like '%BT%' and hspt_subcode = #{hspt_subcode}

select idx, id, pwd, name, reg_date, hspt_name, hspt_subname, hspt_subcode, hspt_position, hspt_V, 
hspt_K, hspt_B, hspt_X, hspt_Z, user_code
from users__ where user_code like '%BF%' and hspt_subcode = #{hspt_subcode}
